public class Vertex<E> implements Comparable<Vertex<E>> {    private final E data;    private int distance;    /**K     *     * @param data     */    Vertex(E data) {        this.data = data;    }    /**     *     * @return     */    public String toString() {        return data.toString();    }    /**     *     * @return     */    E getData() {        return data;    }    /**     *K     * @return     */    int getDistance() {        return distance;    }    /**     *     * @param d     */    void setDistance(int d) {        distance = d;    }    /**     *     */    void setMax() {        distance = Integer.MAX_VALUE;    }    /**     *     * @param o     * @return     */    @Override    public int compareTo(Vertex<E> o) {        return getDistance() - o.getDistance();    }}