public class Vertex<E> implements Comparable<Vertex<E>>{    private final E data;    private int distance;    public Vertex(E data) {        this.data = data;    }    public String toString(){        return data.toString();    }    public E getData() {        return data;    }    public int getDistance(){        return distance;    }    public void setMax(){        distance = Integer.MAX_VALUE;    }    public void setDistance(int d){        distance = d;    }    @Override    public int compareTo(Vertex<E> o) {        return getDistance() - o.getDistance();    }}